mapper.select:
select

from w_user_login
where 1=1

	and w_login_id=#{wLoginId}


	and w_user_id=#{wUserId}


	and w_login_password=#{wLoginPassword}


	and w_login_account=#{wLoginAccount}


	and w_login_question=#{wLoginQuestion}


	and w_login_status=#{wLoginStatus}


	and w_login_createtime=#{wLoginCreatetime}
